import React from 'react';
import './label.css';
//child component
const Label = (props)=>{
    let classData = props.noDataClass;
    var btnGroupClasses = classNames(
     classData,
        {
          'show': props.bookList.length > 0,
          'hidden': props.bookList.length <= 0
        }
      );
return(
    <label className="{btnGroupClasses}"> {props.text}</label>
)
}
export default Label;
