import React from 'react';
import { Component } from 'react';
import BookDashboard from '../BookDashboard/BookDashboard'
import Button from '../Button/Button';
import { connect } from 'react-redux';
import * as actionCreator from '../../actions/AddBookAction';

//container 

class Home extends Component {
    addBookHandler = () => {
        this.props.manageModal(true, 'add');
    }

    editBookHandler = () => {
        this.props.manageModal(true, 'edit');
    }
    render() {

        return (
            <div>
                <Button text="Add" parentClickHandler={this.addBookHandler} />
                <Button text="Edit" state={this.props.isEditDisabled} parentClickHandler={this.editBookHandler} />
                <SearchBook parentSearchHandler={this.handleSearch} />

                <BookDashboard />
            </div>
        )
    }

}
const mapStateToProps = store => {
    return {
        books: store.books,
        selectedIndex: store.selectedIndex,
        isEditDisabled:store.isEditDisabled
    };
};

const mapDispatchToProps = dispatch => {
    return {
        manageModal: (showModal, action) => {
            return dispatch(actionCreator.manageModal(showModal, action))
        },
    };
};


export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Home);
